export const initialState = {
  logInLoading: false, // 로그인 시도중
  logInDone: false,
  logInError: null,
  logOutLoading: false, // 로그아웃 시도중
  logOutDone: false,
  logOutError: null,
  singUpLoading: false, // 회원 가입 시도중
  singUpDone: false,
  singUpError: null,
  changeNicnameLoading: false, // 닉네임 변경 시도중
  changeNicnameDone: false,
  changeNicnameError: null,

  me: null,
  signUpData: {},
  loginData: {},
};

export const LOG_IN_REQUEST = 'LOG_IN_REQUEST';
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS';
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE';

export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST';
export const LOG_OUT_SUCCESS = 'LOG_OUT_SUCCESS';
export const LOG_OUT_FAILURE = 'LOG_OUT_FAILURE';

export const SIGN_UP_REQUEST = 'SIGN_UP_REQUEST';
export const SIGN_UP_SUCCESS = 'SIGN_UP_SUCCESS';
export const SIGN_UP_FAILURE = 'SIGN_UP_FAILURE';

export const CHANGE_NICKNAME_REQUEST = 'SIGN_UP_REQUEST';
export const CHANGE_NICKNAME_SUCCESS = 'SIGN_UP_SUCCESS';
export const CHANGE_NICKNAME_FAILURE = 'SIGN_UP_FAILURE';

export const FOLLOW_REQUEST = 'FOLLOW_REQUEST';
export const FOLLOW_SUCCESS = 'FOLLOW_SUCCESS';
export const FOLLOW_FAILURE = 'FOLLOW_FAILURE';

export const ADD_POST_TO_ME = 'ADD_POST_TO_ME';
export const REMOVE_POST_OF_ME = 'REMOVE_POST_OF_ME';

const dummyUser = (data) => ({
  ...data,
  nickname: '제로초',
  id: 1,
  Posts: [{ id: 1 }],
  Followings: [{ nickname: '부기초' }, { nickname: '상호' }, { nickname: '동규' }],
  Followers: [{ nickname: '부기초' }, { nickname: '상호' }, { nickname: '동규' }],
});

export const loginRequestAction = (data) => {
    return {
        type:LOG_IN_REQUEST,
        data,
    }
}


export const logoutRequestAction = () => {
    return {
        type:LOG_OUT_REQUEST,
    }
}


const reducer = (state = initialState, action) => {
    return produce(state, (draft) => {
        console.log(action, "액션 확인", action.data)
        switch (action.type) {
            case LOG_IN_REQUEST:
                return {
                        ...state,
                       logInLoading:true,
                       logInDone:false,
                       logInError:null,
                    }
                case LOG_IN_SUCCESS:
                    return {
                            ...state,
                            logInLoading:false,
                            logInDone:true,
                            me:dummyUser(action.data),
                    }
                case LOG_IN_FAILURE:
                        return {
                                ...state,
                                logInLoading:false,
                                logInError:action.error,         
                        }
                case LOG_OUT_REQUEST:
                    return {
                            ...state,
                           logOutLoading:true,
                           logOutDone:false,
                           logOutError:null
                    };
                
                case LOG_OUT_SUCCESS:
                    return {
                            ...state,
                            logOutLoading:false,
                            logOutDone:true,
                            me:null,
                      
                    };
                
                case LOG_OUT_FAILURE:
                    return {
                            ...state,
                            logOutLoading:false,
                            logOutError:action.error,
                    };
                 
                    case SIGN_UP_REQUEST:
                        return {
                                ...state,
                               singUpLoading:false,
                               singUpDone:false,
                               singUpError:null,
                        };
                    
                    case SIGN_UP_SUCCESS:
                        return {
                                ...state,
                                singUpLoading:false,
                                singUpDone:true,
                        };
                    
                    case SIGN_UP_FAILURE:
                        return {
                                ...state,
                                changeNicknameLoading:false,
                                changeNicknameError:action.error,
                        };
    
                        case CHANGE_NICKNAME_REQUEST:
                            return {
                                    ...state,
                                   changeNicknameLoading:false,
                                   changeNicknameDone:false,
                                   changeNicknameError:null,
                            };
                        
                        case CHANGE_NICKNAME_SUCCESS:
                            return {
                                    ...state,
                                    changeNicknameLoading:false,
                                    changeNicknameDone:true,
                            };
                   
                        case CHANGE_NICKNAME_FAILURE:
                        return {
                            ...state,
                            changeNicknameLoading: false,
                            changeNicknameError: action.error,
                        };
                        case ADD_POST_TO_ME:
                        return {
                            ...state,
                            me: {
                            ...state.me,
                            Posts: [{ id: action.data }, ...state.me.Posts],
                                            },
                                        };
                        case REMOVE_POST_OF_ME:
                        return {
                            ...state,
                            me: {
                                    ...state.me,
                                    Posts: state.me.Posts.filter((v) => v.id !== action.data),
                                },
                            };
                            default:
                            return state;
        }
    });
};
  
export default reducer;
