import AppLayout from '../components/AppLayout';
import React from 'react';
import { useSelector } from 'react-redux';

const Home = () => {
  const {isLoggedIn} = useSelector((state) => state.user);
  const mainPosts = useSelector((state) => state.post.mainPosts);
  return (
    <AppLayout>
     <PostForm />
     <PostCard />
    </AppLayout>
  );
};

export default Home;
