import { all, fork } from 'redux-saga/effects';
import axios from 'axios';

import postSage from '../sagas/post';
import userSage from '../sagas/user';

axios.defaults.baseURL = 'http://locallhost:3065';

export default function* rootSaga() {
  yield all([
    fork(userSage),
    fork(postSage),

  ]);
}
