export const initialState = {
    isLoggingIn:false,  //로그인 시도중
    isLoggedIn:false,
    isLoggingOut:false, // 로그아웃 시도중

    me:null,
    signUpData:{},
    loginData:{},
}

export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS'
export const LOG_IN_REQUEST = 'LOG_IN_REQUEST'
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE'

export const LOG_OUT_SUCCESS = 'LOG_IN_SUCCESS'
export const LOG_OUT_REQUEST = 'LOG_IN_REQUEST'
export const LOG_OUT_FAILURE = 'LOG_IN_FAILURE'



export const loginRequestAction = (data) => {
    return {
        type:'LOG_IN_REQUEST',
        data,
    }
}


export const logoutRequestAction = () => {
    return {
        type:'LOG_OUT_REQUEST',
    }
}


const reducer = (state = initialState, action) => {
    console.log(action, "액션 확인", action.data)
    switch (action.type) {
        case 'LOG_IN_REQUEST':
            console.log('reducer login')
            return {
                    ...state,
                    isLoggingIn:true,
                    me:action.data,
            }
            case 'LOG_IN_SUCCESS':
                return {
                        ...state,
                        isLoggingIn:true,
                        isLoggedIn:true,
                        me:{...action.data, nickname:'zerocho'},
                }
            case 'LOG_IN_FAILURES':
                    return {
                            ...state,
                            isLoggingIn:false,
                            isLoggedIn:false,         
                    }
            case 'LOG_OUT_REQUEST':
                return {
                        ...state,
                       isLoggingOut:true,
                };
            
            case 'LOG_OUT_SUCCESS':
                return {
                        ...state,
                        isLoggingOut:false,
                        isLoggedIn:false,
                        me:null,
                  
                };
            
            case 'LOG_OUT_FAILURE':
                return {
                        ...state,
                        isLoggingOut:false,   
                };
        default:
            return state;
    }
}

export default reducer;
