import styled, { createGlobalStyle } from 'styled-components';
import { CloseOutlined } from '@ant-design/icons';



const Overlay = styled.div`
position:fixed;
z-index:5000;
top:0;
left:0;
right:0;
bottom:0;
`;
const Header = styled.header`
 header:44px;
 background:white;
 position:relative;
 padding:0;
 text-align:center;

 & h1 {
    margin: 0;
    font-size: 17px;
    color: #333;
    line-height: 44px;
  }

  & button {
  position: absolute;
  right: 0;
  top: 0;
  padding: 15px;
  line-height: 14px;
  cursor: pointer;
  }
`;

const Slickwrapper = styled.div`
    height: calc(100% - 44px);
    background: #090909;
`;
const ImagWrapper = styled.div`
padding:32px;
text-align:center;

& img {
    margin: 0 auto;
    max-height:750px;
  }
`;

const Indicator = styled.div`
 text-align: center;
  
  & > div {
    width: 75px;
    height: 30px;
    line-height: 30px;
    border-radius: 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color: white;
    font-size: 15px;
  }
`;

const Global = createGlobalStyle`
.slick-slide {
    display:inline-block;
}
`;

const CloseBtn = styled(CloseOutlined)`
position:absolute;
right:0;
top:0;
padding:15px;
line-height:14px;
cursor:pointer;
`
