import { all, fork, put, delay, takeLatest, throttle } from "@redux-saga/core/effects";
import axios from 'axios';
import {
        LOG_IN_FAILURE, LOG_IN_SUCCESS, LOG_IN_REQUEST,
        LOG_OUT_FAILURE, LOG_OUT_SUCCESS, LOG_OUT_REQUEST,
        SIGN_UP_FAILURE, SIGN_UP_SUCCESS, SIGN_UP_REQUEST,
        FOLLOW_FAILURE, FOLLOW_SUCCESS, FOLLOW_REQUEST,
        UNFOLLOW_FAILURE, UNFOLLOW_SUCCESS, UNFOLLOW_REQUEST,
} from '../reducers/user';

function followAPI(data) {
    return axios.post('/api/follow', data)
  } 
  
  function* follow(action) {    // 일로 들어옴
      try {
  //    const result = yield call(followAPI,action.data) //첫번쨰 자리는 함수고 나머지는 인수들 
     yield delay(1000);
     yield put({
         type:FOLLOW_SUCCESS,
         data:action.data,
      });
     } catch (err) {
         yield put({
             type:FOLLOW_FAILURE,
             error:err.data
         })
     }
  }

  function unfollowAPI(data) {
    return axios.post('/api/unfollow', data)
  } 
  
  function* unfollow(action) {    // 일로 들어옴
      try {
  //    const result = yield call(logInAPI,action.data) //첫번쨰 자리는 함수고 나머지는 인수들 
     yield delay(1000);
     yield put({
         type:UNFOLLOW_SUCCESS,
         data:action.data
      });
     } catch (err) {
         yield put({
             type:UNFOLLOW_FAILURE,
             error:err.data
         })
     }
  }
function logInAPI(data) {
    return axios.post('/api/login', data)
  } 
  
  function* logIn(action) {    // 일로 들어옴
      try {
  //    const result = yield call(logInAPI,action.data) //첫번쨰 자리는 함수고 나머지는 인수들 
     yield delay(1000);
     yield put({
         type:LOG_IN_SUCCESS,
         data:action.data
      });
     } catch (err) {
         yield put({
             type:LOG_IN_FAILURE,
             error:err.data
         })
     }
  }
  
  function logOutAPI() {
      return axios.post('/api/logout')
    }

    function* logOut() {
        try {
          
      //  const result = yield call(logOutAPI)
      yield delay(1000);
       yield put({
           type:LOG_OUT_SUCCESS,
        //    data:result.data
        });
       } catch (err) {
           yield put({
               type:LOG_OUT_FAILURE,
               error:err.data
           })
       }
    }
  
      
  function signUpAPI() {
    return axios.post('/api/signUp')
  }

  function* signUp() {
      try {
        
    //  const result = yield call(signUpAPI)
    yield delay(1000);
     yield put({
         type:SIGN_UP_SUCCESS,
      //    data:result.data
      });
     } catch (err) {
         yield put({
             type:SIGN_UP_FAILURE,
             error:err.data
         })
     }
  }




function* watchLogin() {
    yield takeLatest(LOG_IN_REQUEST, logIn);
}
function* watchLogOut() {
    yield takeLatest(LOG_OUT_REQUEST, logOut);
}
function* watchSignUp() {
    yield throttle(2000, SIGN_UP_REQUEST, signUp);
}
function* watchFollow() {
    yield throttle(2000, FOLLOW_REQUEST, follow);
}
function* watchUnfollow() {
    yield throttle(2000, UNFOLLOW_REQUEST, unfollow);
}
export default function* userSaga() {
    yield all([
     fork(watchFollow),
     fork(watchUnfollow),
     fork(watchLogin),
     fork(watchLogOut),
     fork(watchSignUp),
    ]);
}
